import React, {Component} from 'react';
import {Input , Button, InputGroup, InputGroupAddon} from 'reactstrap';
import logo from './logo.svg';
import './App.css';
import Translate from '@google-cloud/translate';
//import { translation } from './translateAPI'

class App extends Component {
  state = { englishInput: ''};

  updateInput = event => {
    console.log('event.target.value', event.target.value);
    this.setState({ englishInput: event.target.value})
  };

  viewState = () => {
    console.log(this.translation(this.state.englishInput))
  }

  translation = (text) =>{
    const projectId = 'translationbot-1560840064175';
    const translate = new Translate({projectId});
    const target = 'vn';
    const [translation] = translate.translate("Hello", target);
    console.log(`Text: ${"Hello"}`);
    console.log(`Translation: ${translation}`);
  }

  render() {
    return (
      <div>
      <InputGroup>
        <Input onChange={this.updateInput} />
        <InputGroupAddon addonType='append'><Button onClick={this.viewState}>Send!</Button></InputGroupAddon>
      </InputGroup>
      </div>
    );
    }
}

export default App;
