const translate = require('google-translate-api');

export async function translation(text) {
    const origin = 'en'
    const target = 'vn';
    const [translation] = await translate(text, {from: origin, to: target})
    console.log(`Text: ${text}`);
    console.log(`Translation: ${translation}`);
}
