const translate = require('google-translate-api');

export async function translation(text) {
    const origin = `en`;
    const target = `vn`;
    translate(text, {from: origin, to: target}).then(res => {
        // console.log(`Text: ${text}`);
        // console.log(`Translation: ${res.text}`);
        return res.text
    }).catch(err =>{
        console.error(err);
    });

}
